drop table if exists candidaturas; create table candidaturas ( id uuid primary key default gen_random_uuid(), user_id uuid references auth.users not null, empresa text not null, puesto text not null, estado text not null, fecha date not null, created_at timestamp with time zone default now() ); alter table candidaturas enable row level security; create policy "Usuarios pueden gestionar sus propias candidaturas" on candidaturas for all using (auth.uid() = user_id);
